mapper.select:
SELECT A.*, B.* FROM( SELECT TT1.rnum AS num, TT1.seq, TT1.title, TT1.read_cnt AS "readCnt", TT1.div, TT1.mod_id AS "modId", DECODE(TO_CHAR(SYSDATE, 'YYYYMMDD'), TO_CHAR(TT1.mod_dt, 'YYYYMMDD') , TO_CHAR(TT1.mod_dt, 'HH24:MI') , TO_CHAR(TT1.mod_dt, 'YYYY-MM-DD')) AS "modDt" FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT * FROM PC_BOARD WHERE seq > 0
ORDER BY MOD_DT DESC )T1 WHERE ROWNUM <= #{pageSize} * (#{pageNum} - 1) + #{pageSize} )TT1 WHERE rnum >= #{pageSize} * (#{pageNum} - 1) + 1 )A CROSS JOIN (SELECT COUNT(*) AS totalCnt FROM PC_BOARD WHERE seq > 0
)B

mapper.update:
    UPDATE pc_board
	SET read_cnt = NVL(read_cnt, 0) + 1
	WHERE seq = #{seq}
